void EXTI0_IRQHandler()
{
	char msgToSend[] = "Hello World\n";
	if ( EXTI->PR & 0x1 )
	{
		EXTI->PR |= ( 0x1U << 0U);

		SPI_TransmitData(&SPI_Handle, (uint8_t*)msgToSend, strlen(msgToSend));

	}
}

