	EXTI_LineConfig(EXTI_PortSource_GPIOA, EXTI_Source_0);

	EXTI_InitStruct.EXTI_LineCmd = ENABLE;
	EXTI_InitStruct.EXTI_LineNumber = EXTI_Source_0;
